import React from 'react';
import './App.css';

const CodeCard = ({ title, code, difficulty, language }) => {
  // Function to handle copying code to clipboard
  const handleCopyClick = () => {
    navigator.clipboard.writeText(code) // Use the Clipboard API
      .then(() => {
        alert('Code copied to clipboard!');
      })
      .catch((err) => {
        alert('Failed to copy code: ', err);
      });
  };

  return (
    <div className="code-card">
      <div className={`code-card-sidebar ${difficulty}`}></div> {/* Sidebar */}
      <div className="code-card-content">
        <h3 className="code-title">{title}</h3>
        <pre className="code-content">{code}</pre>

        <div className="code-details">
          <span className={`difficulty-tag ${difficulty}`}>{difficulty}</span>
          <span className="language-tag">{language}</span>
          
          {/* Copy Button */}
          <button 
            className="copy-button"
            onClick={handleCopyClick}
          >
            Copy Code
          </button>
        </div>
      </div>
    </div>
  );
};

export default CodeCard;
