import React from 'react';
import './App.css';

const CodeCard = ({ title, code, difficulty, language }) => {
  const handleCopy = () => {
    // Create a temporary textarea element to copy the code text
    const textArea = document.createElement('textarea');
    textArea.value = code;
    document.body.appendChild(textArea);
    textArea.select();
    document.execCommand('copy');  // Copy the content
    document.body.removeChild(textArea); // Remove the textarea after copying

    // Optionally, you can show an alert or change the button text to indicate success
    alert('Code copied to clipboard!');
  };

  return (
    <div className="code-card">
      <div className={`code-card-sidebar ${difficulty}`}></div>
      <div className="code-card-content">
        <h3 className="code-title">{title}</h3>
        <pre className="code-content">{code}</pre>
        <div className="code-details">
          <span className={`difficulty-tag ${difficulty}`}>{difficulty}</span>
          <span className="language-tag">{language}</span>
        </div>
        {/* Copy Button */}
        <button className="copy-button" onClick={handleCopy}>
          Copy Code
        </button>
      </div>
    </div>
  );
};

export default CodeCard;
