import React, { useState } from 'react';
import './App.css';

const CodeCard = ({ title, code, difficulty, language }) => {
  const [isCopied, setIsCopied] = useState(false);

  // Function to copy code to clipboard
  const handleCopy = () => {
    navigator.clipboard.writeText(code);
    setIsCopied(true);
    setTimeout(() => setIsCopied(false), 2000); // Reset copy state after 2 seconds
  };

  return (
    <div className="code-card">
      <div className={`code-card-sidebar ${difficulty}`}></div> {/* Sidebar indicating difficulty */}
      <div className="code-card-content">
        <h3 className="code-title">{title}</h3>
        <div className="code-box">
          <pre className="code-content">{code}</pre>
          <button className="copy-btn" onClick={handleCopy}>
            {isCopied ? 'Copied!' : 'Copy'}
          </button>
        </div>
        <div className="code-details">
          <span className={`difficulty-tag ${difficulty}`}>{difficulty}</span>
          <span className="language-tag">{language}</span>
        </div>
      </div>
    </div>
  );
};

export default CodeCard;
